div.p:
.card{
    padding: 2em;
    background-image: linear-gradient(135deg, black, #251132, darkslateblue);
    border-radius: 10px;
    position: relative;
    color: white;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 1.1em;
    line-height: 1.5em;
}

.card h2 {
    font-size: 1.5em;
    margin: 0;
}

.card h3 {
    color: #d39bff;
    font-size: 1.1em;
}

.card p {
    margin: 0.75rem 0 0;
}

.card a {
    color: #EF4436;
    text-decoration: none;
}


:where(.gridcontainer ol, .gridcontainer ul) {
    margin-inline-start: 0;
    padding-inline-start: 30px;
}

@property --angle{
    syntax: "<angle >";
    initial-value: 0deg;
    inherits: false;
}

.card::after, .card::before{
    content: '';
    position: absolute;
    height: 100%;
    width: 100%;
    background-image: conic-gradient(from var(--angle), #EF4436, darkslateblue, #251132, darkslateblue, #EF4436);
    top: 50%;
    left: 50%;
    translate: -50% -50%;
    z-index: -1;
    padding: 3px;
    border-radius: 10px;
    animation: 5s spin linear 1;
}

.card::before{
    filter: blur(1.2rem);
    opacity: 0.60;
}

@keyframes spin{
    from{
        --angle: 0deg;
    }
    to{
        --angle: 360deg;
    }
}

.gridcontainer {
    background-color: #1c1e25;
    display: grid;
    grid-gap: 1.5rem;
    padding-block: 2rem;
    width: min(90%, 70rem);
    margin-inline: auto;
}

@media (min-width: 50em) {
    .grid-span-2 {
        grid-column: span 2;
    }

    .gridcontainer {
        grid-template-columns: repeat(3, 1fr);
    }
}

.card:nth-child(5) {
    grid-row: span 2;
}

li, ol {
    line-height: 1.4em;
    padding-bottom: .5em;
}

.area-skills p {
    padding-left: 1em;
}
